mapper.update:

update cart set order_quantity=#{order_quantity} 
where orderer_id=#{orderer_id}
and product_id=#{product_id}

mapper.delete:

delete from cart 
where orderer_id = #{orderer_id}
and product_id = #{product_id}

mapper.select:

select count(*) from cart
where orderer_id = #{orderer_id} 
and product_id = #{product_id}
